// Search Microsoft Create templates
const searchResults = await searchTemplates({
  query: 'architecture newsletter',
  category: 'Business',
  contentType: 'Word'
});

console.log(`Found ${searchResults.length} templates`);
console.log('Top match:', searchResults[0].name);

// Output:
// Found 12 templates
// Top match: Architecture newsletter

// Discover all content controls
const richText = context.document.contentControls;
richText.load('items');
await context.sync();

for (let i = 0; i < richText.items.length; i++) {
    richText.items[i].load(['placeholderText', 'subtype', 'title', 'tag']);
}
await context.sync();

console.log(`Total Content Controls Found: ${richText.items.length}`);
console.log('\nKey sections identified:');
console.log('- Page 1: Cover with "WHAT YOU NEED TO KNOW" headline');
console.log('- Page 2: Real Estate section + Design Trends');
console.log('- Page 3: Practical section + Natural Light guide');
console.log('- Page 4: Rising Stars / Team Spotlight');

// Output:
// Total Content Controls Found: 27
// 
// Key sections identified:
// - Page 1: Cover with "WHAT YOU NEED TO KNOW" headline
// - Page 2: Real Estate section + Design Trends
// - Page 3: Practical section + Natural Light guide
// - Page 4: Rising Stars / Team Spotlight

// Fill Page 1 - Cover & Main Story
controls[6].insertText('NORTH PIER LIBRARY GREENLIT', Word.InsertLocation.replace);

controls[7].insertText(
  'City Council voted 9–1 to approve the $38.2M North Pier Community Library. ' +
  'Groundbreaking is set for October 28. The 38,000-sf building will feature a ' +
  'mass timber reading room, 150-seat auditorium, maker spaces, and rooftop garden. ' +
  'Expected completion: Q2 2027. "We want a building that invites sunlight and ' +
  'neighbors. The design does both," said City Librarian Lea Franklin.',
  Word.InsertLocation.replace
);

// Fill Page 2 - Market Snapshot
controls[12].insertText('OFFICE-TO-RESIDENTIAL CONVERSIONS', Word.InsertLocation.replace);
controls[13].insertText('TOP US MARKETS Q3 2025', Word.InsertLocation.replace);
controls[14].insertText(
  'Austin (19.6% vacancy): 5 conversion projects, including Broadmoor tower → 310 lofts\n' +
  'Chicago (24.0%): LaSalle Reimagined adds 3 projects\n' +
  'Denver (21.4%): 3 CBD towers slated for mixed-use\n' +
  'Raleigh (17.1%): Green Street Labs → 220 apartments\n' +
  'Tampa (15.8%): Harbor Center conversion approved',
  Word.InsertLocation.replace
);

// Fill Design Trends
controls[15].insertText('MASS TIMBER MOMENTUM', Word.InsertLocation.replace);
controls[16].insertText('Lower carbon, warm aesthetics', Word.InsertLocation.replace);
controls[17].insertText(
  'Mass timber delivers 25–45% less embodied carbon than concrete/steel. ' +
  'Code acceptance expanding (2021 IBC). Real examples: Ascent Milwaukee (25 stories), ' +
  'T3 Minneapolis, Intro Cleveland. Watch-outs: fire engineering, moisture protection.',
  Word.InsertLocation.replace
);

// Fill Page 3 - Natural Light Guide
controls[21].insertText('HARNESSING NATURAL LIGHT', Word.InsertLocation.replace);
controls[22].insertText('Strategic skylight placement', Word.InsertLocation.replace);
controls[23].insertText(
  'Orient skylights NE–NW for even light. Use light shelves on south façades at ' +
  '10–14 ft height. Add clerestories over corridors with ceiling reflectance >0.7. ' +
  'Include glare control: low-VLT glass + exterior shading.',
  Word.InsertLocation.replace
);

// Fill Page 4 - Team Spotlight
controls[24].insertText('TEAM SPOTLIGHT', Word.InsertLocation.replace);
controls[25].insertText('Maya Ortega wins AIA Chicago Award', Word.InsertLocation.replace);
controls[26].insertText(
  'Congratulations to Maya Ortega, Junior Designer, for winning the AIA Chicago ' +
  'Emerging Professionals Award 2025! Maya led daylight analysis for the North Pier ' +
  'Library reading room using Radiance. "Good light helps people linger. We design ' +
  'for lingering," says Maya.',
  Word.InsertLocation.replace
);

await context.sync();
console.log('Newsletter content filled successfully!');
