// Search Microsoft Create templates
const searchResults = await searchTemplates({
  query: 'school newsletter',
  category: 'Education',
  contentType: 'Word'
});

console.log(`Found ${searchResults.length} templates`);
console.log('Top matches:');
console.log('1.', searchResults[0].name);
console.log('2.', searchResults[1].name);
console.log('3.', searchResults[2].name);

// Output:
// Found 15 templates
// Top matches:
// 1. Back to school newsletter
// 2. Elementary school newsletter
// 3. PTA newsletter

// Discover all content controls in the template
const contentControls = context.document.contentControls;
contentControls.load('items');
await context.sync();

for (let i = 0; i < contentControls.items.length; i++) {
    contentControls.items[i].load(['placeholderText', 'title']);
}
await context.sync();

console.log(`Found ${contentControls.items.length} content sections in the template`);
console.log('\nKey sections identified:');
console.log('- Header with school name and issue date');
console.log('- Principal\'s welcome message');
console.log('- Upcoming events calendar');
console.log('- Recent highlights section');
console.log('- Counselor\'s corner');
console.log('- Monthly focus area');
console.log('- Book recommendation');
console.log('- Quote of the month');
console.log('- Contact information footer');

// Output:
// Found 18 content sections in the template
// 
// Key sections identified:
// - Header with school name and issue date
// - Principal's welcome message
// - Upcoming events calendar
// - Recent highlights section
// - Counselor's corner
// - Monthly focus area
// - Book recommendation
// - Quote of the month
// - Contact information footer

// Fill header information
await fillContentControl('School Name', 'ROOSEVELT MIDDLE SCHOOL');
await fillContentControl('Newsletter Title', 'BACK TO SCHOOL');
await fillContentControl('Issue Date', 'SEPTEMBER 2025');

// Fill welcome message
await fillContentControl('Principal Name', 'Dr. Elena Alvarez');
await fillContentControl('Welcome Message', 'After a summer of building our new makerspace and refreshing the courtyard, we\'re excited to see students, families, and staff back on campus. We believe this year will be about curiosity, kindness, and stretching ourselves.\n\nPlease check bus routes + start times (8:05 first bell). Big thanks to volunteers who helped with the locker cleanup.\n\nReminder: paperwork packets due by 9/9; free breakfast for all students; this month\'s attendance goal is 95%+.');

// Fill upcoming events
await fillContentControl('Events Title', 'UPCOMING EVENTS');
await fillContentControl('Events List', '• 9/5 - Locker setup & schedule pick-up, 8:30–11:30am in the gym\n\n• 9/10 - Back-to-School Night 6–8pm — start in the auditorium, childcare room 104\n\n• 9/13 - Picture Day; forms go home on 9/9; retakes 10/11\n\n• 9/17 - PTA Ice Cream Social 3:30 pm, courtyard\n\n• 9/25 - Volleyball tryouts (7th/8th), 3:15–5:00 pm, bring sneakers!\n\n• 9/30 - Coding Club kickoff, 3:15-4:30pm, Room 208');

// Fill recent highlights
await fillContentControl('Highlights Title', 'RECENT HIGHLIGHTS');
await fillContentControl('Highlights Content', '• Robotics team made Top 10 at the regional VEX challenge (Coach Patel)\n\n• 7th-grade mural unveiled on north hallway — student artists: Alina G., Mateo R., David C.\n\n• Summer reading challenge: 312 participants; grade-level winners = 6th: James N., 7th: Priya S., 8th: Teo L.\n\n• Our cafeteria now offers a daily vegetarian option; compost bins added');

// Fill counselors' updates
await fillContentControl('Counselors Title', 'FROM THE COUNSELORS');
await fillContentControl('Counselors Names', 'Ms. Park & Mr. Ramos');
await fillContentControl('Counselors Content', '• First week drop-in hours 8:00–11:00am (Room 112)\n\n• Schedule changes accepted through Fri 9/12 at 4 pm\n\n• Mental health resources: "Calm Corner" open in library; confidential referrals via bit.ly/rms-care\n\n• New Student Q&A lunch 9/9 (bring questions)\n\n• HS info night: late Oct (tentative 10/29), watch email');

// Fill monthly focus
await fillContentControl('Focus Title', 'SEPTEMBER FOCUS');
await fillContentControl('Focus Content', 'Attendance challenge 95%+, "Kindness chain" actions around campus (leave a note, help a classmate, thank a staff member), and digital citizenship mini-lessons in advisory. Homerooms will track progress — prize = extra recess on 9/30.');

// Fill book recommendation
await fillContentControl('Book Title', 'BOOK OF THE MONTH');
await fillContentControl('Book Details', '"New Kid" — Jerry Craft (HarperCollins, 2019)');
await fillContentControl('Book Description', 'Jordan Banks transfers to a prestigious academy and navigates new expectations while holding onto his love of art. This graphic novel explores starting at a new school, friendships, and finding your voice. Reading level approx 5–8; copies in the library; audiobook via Sora.');

// Fill quote
await fillContentControl('Quote', '"Reading is a passport to countless adventures." — Mary Pope Osborne');

// Fill contact information
await fillContentControl('Contact Info', 'office: 718-555-0145 | ealvarez@rooseveltrms.org | rooseveltrms.org | @RMSRockets');

await context.sync();
console.log('Newsletter content filled successfully!');

// Helper function to fill content controls
async function fillContentControl(title, content) {
  const controls = context.document.contentControls;
  controls.load('items');
  await context.sync();

  for (let i = 0; i < controls.items.length; i++) {
    const control = controls.items[i];
    control.load('title');
    await context.sync();

    if (control.title === title) {
      control.insertText(content, Word.InsertLocation.replace);
      return;
    }
  }
}
