console.log('Menu content filled successfully!');

// Helper function to fill content controls
async function fillContentControl(title, content) {
  const controls = context.document.contentControls;
  controls.load('items');
  await context.sync();

  for (let i = 0; i < controls.items.length; i++) {
    const control = controls.items[i];
    control.load('title');
    await context.sync();

    if (control.title === title) {
      control.insertText(content, Word.InsertLocation.replace);
      return;
    }
  }
}
