// Helper function to fill content controls
async function fillContentControl(title, content) {
  const controls = context.document.contentControls;
  controls.load('items');
  await context.sync();

  for (let i = 0; i < controls.items.length; i++) {
    const control = controls.items[i];
    control.load('title');
    await context.sync();

    if (control.title === title) {
      if (Array.isArray(content)) {
        // Format menu items
        let formattedContent = '';
        content.forEach(item => {
          formattedContent += `${item.name} ${item.notes ? '(' + item.notes + ')' : ''}\n`;
          formattedContent += `${item.description}\n`;
          formattedContent += `$${item.price}\n\n`;
        });
        control.insertText(formattedContent.trim(), Word.InsertLocation.replace);
      } else {
        control.insertText(content, Word.InsertLocation.replace);
      }
      return;
    }
  }
}
