// Check Word connection status
const wordConnection = await checkWordConnection();

console.log('Connection Status:', wordConnection.status);
console.log('Document ID:', wordConnection.documentId);
console.log('Uptime:', wordConnection.uptime);

// Output:
// Connection Status: connected
// Document ID: b08c68f5...
// Uptime: 2058.8s

// Clear the document and create the title
context.document.body.clear();

// Add the main title
const title = context.document.body.insertParagraph("Microsoft Corporation", Word.InsertLocation.start);
title.font.size = 28;
title.font.bold = true;
title.font.color = "#1F4788";
title.alignment = Word.Alignment.centered;
title.spaceAfter = 6;

// Add subtitle
const subtitle = context.document.body.insertParagraph("Q1 FY2026 Financial Analysis Report", Word.InsertLocation.end);
subtitle.font.size = 20;
subtitle.font.color = "#2B579A";
subtitle.alignment = Word.Alignment.centered;
subtitle.spaceAfter = 12;

// Add report date
const reportDate = context.document.body.insertParagraph("Fiscal Quarter Ended: September 30, 2025", Word.InsertLocation.end);
reportDate.font.size = 12;
reportDate.font.italic = true;
reportDate.alignment = Word.Alignment.centered;
reportDate.spaceAfter = 24;

// Add a line separator
const separator = context.document.body.insertParagraph("_____________________________________________________________________________", Word.InsertLocation.end);
separator.font.color = "#D0D0D0";
separator.alignment = Word.Alignment.centered;
separator.spaceAfter = 24;

await context.sync();
return "Title and header created successfully";

// Add page numbers and final formatting
const sections = context.document.sections;
sections.load("items");
await context.sync();

// Add page numbers in footer using insertText
const primaryFooter = sections.items[0].getFooter("primary");
primaryFooter.clear();
const footerPara = primaryFooter.insertParagraph("Microsoft Corporation | Q1 FY2026 Financial Analysis", Word.InsertLocation.start);
footerPara.alignment = Word.Alignment.centered;
footerPara.font.size = 9;
footerPara.font.color = "#666666";
footerPara.font.italic = true;

// Set page margins for professional look
sections.items[0].topMargin = 72; // 1 inch
sections.items[0].bottomMargin = 72;
sections.items[0].leftMargin = 72;
sections.items[0].rightMargin = 72;

// Add header
const primaryHeader = sections.items[0].getHeader("primary");
primaryHeader.clear();
const headerPara = primaryHeader.insertParagraph("CONFIDENTIAL - Financial Analysis Report", Word.InsertLocation.start);
headerPara.alignment = Word.Alignment.right;
headerPara.font.size = 9;
headerPara.font.color = "#999999";
headerPara.font.italic = true;

await context.sync();
return "Document formatting completed successfully";
