// Insert title and executive summary section with HTML
const htmlContent = `
<html>
<head>
<style>
body { font-family: 'Calibri', sans-serif; line-height: 1.6; color: #333; }
h1 { color: #1a472a; font-size: 28pt; text-align: center; margin-bottom: 10pt; }
h2 { color: #2c5282; font-size: 18pt; margin-top: 20pt; margin-bottom: 10pt; border-bottom: 2px solid #2c5282; padding-bottom: 5pt; }
h3 { color: #2c5282; font-size: 14pt; margin-top: 15pt; margin-bottom: 8pt; }
p { margin-bottom: 10pt; text-align: justify; }
.subtitle { text-align: center; font-size: 16pt; color: #666; margin-bottom: 30pt; }
.executive-summary { background-color: #f0f4f8; padding: 15pt; border-left: 4px solid #2c5282; margin: 20pt 0; }
.highlight { background-color: #fffacd; padding: 2pt 4pt; }
.date-info { text-align: center; font-size: 12pt; color: #666; margin-bottom: 20pt; }
</style>
</head>
<body>

<h1>RETAILMAX DIGITAL TRANSFORMATION INITIATIVE</h1>
<div class="subtitle">Comprehensive Project Proposal</div>
<div class="date-info">
<strong>Prepared for:</strong> RetailMax Corporation Board of Directors<br>
<strong>Date:</strong> October 24, 2024<br>
<strong>Project Duration:</strong> January - August 2025<br>
<strong>Total Budget:</strong> $1,200,000
</div>

<div class="executive-summary">
<h2>EXECUTIVE SUMMARY</h2>
<p><strong>Project Title:</strong> RetailMax Digital Transformation Initiative</p>
<p><strong>Strategic Alignment:</strong> Following the Product Strategy Planning Session held on October 22, 2024, with key stakeholders including CEO Sarah Chen, CTO Mike Johnson, CPO Lisa Park, and CFO Tom Wilson, this proposal outlines the comprehensive digital transformation strategy for RetailMax Corporation.</p>
<p><strong>Investment:</strong> $1.2 million over 8 months (January - August 2025)</p>
<p><strong>Expected Returns:</strong></p>
<ul>
<li>35% reduction in operational costs (approximately $1.05 million annual savings)</li>
<li>50% improvement in processing speed and efficiency</li>
<li>Complete system modernization and integration</li>
<li>Enhanced scalability for future growth</li>
<li>ROI payback period: Just over 1 year</li>
</ul>
<p><strong>Key Decisions from Board Meeting:</strong></p>
<ul>
<li>Approved $2M budget for AI feature development (includes this initiative)</li>
<li>Q1 2025 product roadmap finalized</li>
<li>New pricing strategy for enterprise tier</li>
</ul>
</div>

</body>
</html>
`;

await Word.run(async (context) => {
  context.document.body.insertHtml(htmlContent, Word.InsertLocation.end);
  await context.sync();
  return "✅ HTML inserted (73ms)";
});

