const subjectPara = context.document.body.insertParagraph("Re: Software Development Internship Position", Word.InsertLocation.end);
subjectPara.font.bold = true;
subjectPara.spaceAfter = 12;

// Salutation
const salutationPara = context.document.body.insertParagraph("Dear Hiring Manager,", Word.InsertLocation.end);
salutationPara.spaceAfter = 12;

// Opening paragraph
const openingPara = context.document.body.insertParagraph(
    "I am writing to express my interest in the Software Development Internship position advertised on your company website. As a second-year Computer Science student at [University Name], I am eager to apply my technical skills and academic knowledge in a professional environment.",
    Word.InsertLocation.end
);
openingPara.spaceAfter = 12;

// Skills and experience paragraph
const skillsPara = context.document.body.insertParagraph(
    "Through my coursework and personal projects, I have developed proficiency in Python, HTML, and CSS. I have successfully built a responsive website showcasing film reviews and created a Python script for automated file organization. Additionally, I have experience building custom computer systems, demonstrating my understanding of hardware-software integration.",
    Word.InsertLocation.end
);
skillsPara.spaceAfter = 12;

// Soft skills paragraph
const softSkillsPara = context.document.body.insertParagraph(
    "My involvement in competitive online gaming has honed my strategic thinking, teamwork, and problem-solving abilities—skills that translate directly to collaborative software development. I am a quick learner with a strong passion for technology and am committed to contributing meaningfully to your team.",
    Word.InsertLocation.end
);
softSkillsPara.spaceAfter = 12;

// Closing paragraph
const closingPara = context.document.body.insertParagraph(
    "I am available for an internship during the summer months and would welcome the opportunity to discuss how I can contribute to your organization. I am flexible with my schedule for an interview at your convenience.",
    Word.InsertLocation.end
);
closingPara.spaceAfter = 12;

// Thank you
const thanksPara = context.document.body.insertParagraph(
    "Thank you for considering my application. I look forward to hearing from you.",
    Word.InsertLocation.end
);
thanksPara.spaceAfter = 12;

// Closing
const closingLine = context.document.body.insertParagraph("Sincerely,", Word.InsertLocation.end);
closingLine.spaceAfter = 24;

// Name
const namePara = context.document.body.insertParagraph("Ming Li", Word.InsertLocation.end);

// Contact info
const emailPara = context.document.body.insertParagraph("Email: ming.li@university.edu", Word.InsertLocation.end);
const phonePara = context.document.body.insertParagraph("Phone: (XXX) XXX-XXXX", Word.InsertLocation.end);

// Apply consistent formatting
const paragraphs = context.document.body.paragraphs;
paragraphs.load("items");

await context.sync();

// Set font and size for all paragraphs
for (let i = 0; i < paragraphs.items.length; i++) {
    paragraphs.items[i].font.name = "Calibri";
    paragraphs.items[i].font.size = 11;
    paragraphs.items[i].alignment = Word.Alignment.left;
}

await context.sync();

return "Letter has been rewritten professionally";

