actions.previewContent:
// Check Word connection status
const wordConnection = await checkWordConnection();

console.log('Connection Status:', wordConnection.status);
console.log('Document ID:', wordConnection.documentId);
console.log('Uptime:', wordConnection.uptime);

// Output:
// Connection Status: connected
// Document ID: b08c68f5...
// Uptime: 16.1s

// Get document structure and key sections
const body = context.document.body;
const paragraphs = body.paragraphs;
paragraphs.load("text,style");

await context.sync();

// Extract key headings and content structure
let structure = {
    totalParagraphs: paragraphs.items.length,
    headings: [],
    keyContent: []
};

// Get first 50 paragraphs to understand structure
for (let i = 0; i < Math.min(50, paragraphs.items.length); i++) {
    const para = paragraphs.items[i];
    if (para.style && para.style.includes("Heading")) {
        structure.headings.push({
            index: i,
            style: para.style,
            text: para.text.substring(0, 100)
        });
    }
    if (i < 20) {
        structure.keyContent.push({
            style: para.style,
            text: para.text.substring(0, 150)
        });
    }
}

return structure;

// Extract strategic objectives and business value
const searchStrategic = context.document.body.search("Strategic", {matchCase: false});
searchStrategic.load("items");
await context.sync();

let strategicContent = [];
for (let i = 0; i < Math.min(10, searchStrategic.items.length); i++) {
    const result = searchStrategic.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    strategicContent.push(para.text.substring(0, 300));
}

// Extract budget information
const searchBudget = context.document.body.search("budget", {matchCase: false});
searchBudget.load("items");
await context.sync();

let budgetContent = [];
for (let i = 0; i < Math.min(10, searchBudget.items.length); i++) {
    const result = searchBudget.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    budgetContent.push(para.text.substring(0, 300));
}

// Extract ROI and metrics
const searchROI = context.document.body.search("ROI", {matchCase: false});
searchROI.load("items");
await context.sync();

let roiContent = [];
for (let i = 0; i < Math.min(5, searchROI.items.length); i++) {
    const result = searchROI.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    roiContent.push(para.text.substring(0, 300));
}

return {
    strategic: strategicContent,
    budget: budgetContent,
    roi: roiContent
};

// Extract milestones and timeline
const searchMilestone = context.document.body.search("milestone", {matchCase: false});
searchMilestone.load("items");
await context.sync();

let milestoneContent = [];
for (let i = 0; i < Math.min(8, searchMilestone.items.length); i++) {
    const result = searchMilestone.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    milestoneContent.push(para.text.substring(0, 400));
}

// Extract risk information
const searchRisk = context.document.body.search("risk", {matchCase: false});
searchRisk.load("items");
await context.sync();

let riskContent = [];
for (let i = 0; i < Math.min(8, searchRisk.items.length); i++) {
    const result = searchRisk.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    riskContent.push(para.text.substring(0, 400));
}

// Extract technology stack
const searchTech = context.document.body.search("technology", {matchCase: false});
searchTech.load("items");
await context.sync();

let techContent = [];
for (let i = 0; i < Math.min(8, searchTech.items.length); i++) {
    const result = searchTech.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    techContent.push(para.text.substring(0, 400));
}

return {
    milestones: milestoneContent,
    risks: riskContent,
    technology: techContent
};

// Extract phase information and timeline
const searchPhase = context.document.body.search("Phase", {matchCase: false});
searchPhase.load("items");
await context.sync();

let phaseContent = [];
for (let i = 0; i < Math.min(15, searchPhase.items.length); i++) {
    const result = searchPhase.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    if (para.text.includes("Phase") && para.text.length > 20) {
        phaseContent.push(para.text.substring(0, 500));
    }
}

// Extract cost reduction and efficiency metrics
const searchCost = context.document.body.search("35%", {matchCase: false});
searchCost.load("items");
await context.sync();

let costContent = [];
for (let i = 0; i < Math.min(5, searchCost.items.length); i++) {
    const result = searchCost.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    costContent.push(para.text);
}

// Extract processing improvement
const searchProcessing = context.document.body.search("50%", {matchCase: false});
searchProcessing.load("items");
await context.sync();

let processingContent = [];
for (let i = 0; i < Math.min(5, searchProcessing.items.length); i++) {
    const result = searchProcessing.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    processingContent.push(para.text);
}

return {
    phases: phaseContent,
    costReduction: costContent,
    processingImprovement: processingContent
};

// Extract team and resource information
const searchTeam = context.document.body.search("team", {matchCase: false});
searchTeam.load("items");
await context.sync();

let teamContent = [];
for (let i = 0; i < Math.min(10, searchTeam.items.length); i++) {
    const result = searchTeam.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    if (para.text.length > 30) {
        teamContent.push(para.text.substring(0, 400));
    }
}

// Extract resource requirements
const searchResource = context.document.body.search("resource", {matchCase: false});
searchResource.load("items");
await context.sync();

let resourceContent = [];
for (let i = 0; i < Math.min(10, searchResource.items.length); i++) {
    const result = searchResource.items[i];
    const para = result.paragraphs.getFirst();
    para.load("text");
    await context.sync();
    if (para.text.length > 30) {
        resourceContent.push(para.text.substring(0, 400));
    }
}

// Get document style information
const body = context.document.body;
body.load("font");
const firstPara = body.paragraphs.getFirst();
firstPara.load("font,style");

await context.sync();

return {
    team: teamContent,
    resources: resourceContent,
    documentStyle: {
        bodyFont: body.font.name,
        firstParaFont: firstPara.font.name,
        firstParaStyle: firstPara.style
    }
};

// Clear the entire document
const body = context.document.body;
body.clear();

// Set up document formatting
body.font.name = "Calibri";
body.font.size = 11;

await context.sync();

return "Document cleared successfully";

// Check document statistics
const body = context.document.body;
const paragraphs = body.paragraphs;
paragraphs.load("items");

const tables = body.tables;
tables.load("items");

await context.sync();

// Count content elements
let lineCount = 0;
for (let para of paragraphs.items) {
    para.load("text");
}
await context.sync();

for (let para of paragraphs.items) {
    if (para.text.trim().length > 0) {
        lineCount++;
    }
}

return {
    paragraphs: paragraphs.items.length,
    tables: tables.items.length,
    contentLines: lineCount,
    status: "Document condensed to fit 2 pages"
};
