// Check Word connection status
const wordConnection = await checkWordConnection();

console.log('Connection Status:', wordConnection.status);
console.log('Document ID:', wordConnection.documentId);
console.log('Uptime:', wordConnection.uptime);

// Output:
// ✅ Connected to Word (ID: b08c68f5..., uptime: 50.5s)

// Read document HTML content
await Word.run(async (context) => {
  const body = context.document.body;
  const html = body.getHtml();
  await context.sync();

  console.log('HTML export:', html.value.length, 'chars');
  return html.value;
});

// Output:
// ⚠ Large MCP response (~11.3k tokens), this can fill up context quickly
// ✅ HTML export (140ms, 43414 chars)

// Find and replace the title
const titleRange = context.document.body.search("RETAILMAX 数字化转型计划", {matchCase: false});
titleRange.load("items");
await context.sync();

if (titleRange.items.length > 0) {
    titleRange.items[0].insertText("RETAILMAX Digital Transformation Plan", Word.InsertLocation.replace);
}

// Replace the subtitle information
const subtitleRange = context.document.body.search("执行摘要", {matchCase: false});
subtitleRange.load("items");
await context.sync();

if (subtitleRange.items.length > 0) {
    subtitleRange.items[0].insertText("Executive Summary", Word.InsertLocation.replace);
}

// Replace the date and investment info
const dateRange = context.document.body.search("年", {matchCase: false});
dateRange.load("items");
await context.sync();

// Replace specific date patterns
const patterns = [
    {find: "2024 年 10 月", replace: "October 2024"},
    {find: "2025 年 1 月至 8 月", replace: "January to August 2025"},
    {find: "$1.2M 投资", replace: "$1.2M Investment"}
];

for (let pattern of patterns) {
    const searchResults = context.document.body.search(pattern.find, {matchCase: false});
    searchResults.load("items");
    await context.sync();

    if (searchResults.items.length > 0) {
        searchResults.items[0].insertText(pattern.replace, Word.InsertLocation.replace);
    }
}

await context.sync();
return "Title and header translated";

// Translate section headings
const headings = [
    {find: "战略目标和价值主张", replace: "Strategic Objectives and Value Proposition"},
    {find: "预算和时间表", replace: "Budget and Timeline"},
    {find: "关键里程碑", replace: "Key Milestones"},
    {find: "风险缓解", replace: "Risk Mitigation"},
    {find: "投资回报率和成功指标", replace: "ROI and Success Metrics"},
    {find: "技术与基础设施", replace: "Technology & Infrastructure"},
    {find: "团队结构", replace: "Team Structure"},
    {find: "建议", replace: "Recommendation"}
];

for (let heading of headings) {
    const searchResults = context.document.body.search(heading.find, {matchCase: false});
    searchResults.load("items");
    await context.sync();

    if (searchResults.items.length > 0) {
        searchResults.items[0].insertText(heading.replace, Word.InsertLocation.replace);
    }
}

// Translate the main paragraph in Strategic Objectives section
const strategicPara = context.document.body.search("在 10 月 22 日的执行战略会议之后", {matchCase: false});
strategicPara.load("items");
await context.sync();

if (strategicPara.items.length > 0) {
    const para = strategicPara.items[0].paragraphs.getFirst();
    para.load("text");
    await context.sync();

    const newText = "Following the executive strategy session on October 22nd, this plan modernizes RetailMax's legacy systems to unlock growth. Goals: 35% cost reduction ($1.05M/year), 50% faster processing, scalable infrastructure, data-driven capabilities. Investment: $1.2M, using 60% of approved $2M AI budget. ROI: 13.7-month payback, 162% 3-year return.";

    para.insertText(newText, Word.InsertLocation.replace);
}

await context.sync();
return "Section headings and strategic objectives translated";

// Get all tables in the document
const tables = context.document.body.tables;
tables.load("items");
await context.sync();

// First table is Budget and Timeline
if (tables.items.length > 0) {
    const budgetTable = tables.items[0];

    // Translate headers
    budgetTable.getCell(0, 0).body.insertText("Phase", Word.InsertLocation.replace);
    budgetTable.getCell(0, 1).body.insertText("Timeline", Word.InsertLocation.replace);
    budgetTable.getCell(0, 2).body.insertText("Budget", Word.InsertLocation.replace);
    budgetTable.getCell(0, 3).body.insertText("Deliverables", Word.InsertLocation.replace);

    // Translate Phase column
    budgetTable.getCell(1, 0).body.insertText("1. Audit", Word.InsertLocation.replace);
    budgetTable.getCell(2, 0).body.insertText("2. Design", Word.InsertLocation.replace);
    budgetTable.getCell(3, 0).body.insertText("3. POC", Word.InsertLocation.replace);
    budgetTable.getCell(4, 0).body.insertText("4-5. Build", Word.InsertLocation.replace);
    budgetTable.getCell(5, 0).body.insertText("6-7. Deploy", Word.InsertLocation.replace);
    budgetTable.getCell(6, 0).body.insertText("8. Handoff", Word.InsertLocation.replace);

    // Translate Timeline column
    budgetTable.getCell(1, 1).body.insertText("Jan 1-15", Word.InsertLocation.replace);
    budgetTable.getCell(2, 1).body.insertText("Jan 16-Feb 7", Word.InsertLocation.replace);
    budgetTable.getCell(3, 1).body.insertText("Feb 8-28", Word.InsertLocation.replace);
    budgetTable.getCell(4, 1).body.insertText("Mar-May", Word.InsertLocation.replace);
    budgetTable.getCell(5, 1).body.insertText("Jun-Jul", Word.InsertLocation.replace);
    budgetTable.getCell(6, 1).body.insertText("August", Word.InsertLocation.replace);

    // Translate Budget column
    budgetTable.getCell(1, 2).body.insertText("$50K", Word.InsertLocation.replace);
    // Other budget values are already in English format

    // Translate Deliverables column
    budgetTable.getCell(1, 3).body.insertText("Current state analysis", Word.InsertLocation.replace);
    budgetTable.getCell(2, 3).body.insertText("Architecture, tech stack", Word.InsertLocation.replace);
    budgetTable.getCell(3, 3).body.insertText("Validation, approval", Word.InsertLocation.replace);
    budgetTable.getCell(4, 3).body.insertText("Core systems, migration", Word.InsertLocation.replace);
    budgetTable.getCell(5, 3).body.insertText("Testing, go-live", Word.InsertLocation.replace);
    budgetTable.getCell(6, 3).body.insertText("Training, closure", Word.InsertLocation.replace);
}

await context.sync();
return "Budget and Timeline table translated";

// Find the Key Milestones list and translate each item
const lists = context.document.body.lists;
lists.load("items");
await context.sync();

// Translate milestone list items by searching for specific text patterns
const milestones = [
    {find: "1 月 15 日： 审核完成→设计开始", replace: "Jan 15: Audit complete → Design start"},
    {find: "2 月 7 日： 架构批准→ POC 开始", replace: "Feb 7: Architecture approved → POC start"},
    {find: "2 月 28 日： POC 验证→开发授权", replace: "Feb 28: POC validated → Development authorized"},
    {find: "5 月 15 日： 构建完整的→测试阶段", replace: "May 15: Build complete → Testing phase"},
    {find: "7 月 31 日： →培训开始实现上线", replace: "Jul 31: Go-live achieved → Training begins"},
    {find: "8 月 31 日： 95% 的员工接受了项目结束→培训", replace: "Aug 31: Project closure → 95% staff trained"}
];

for (let milestone of milestones) {
    const searchResults = context.document.body.search(milestone.find, {matchCase: false});
    searchResults.load("items");
    await context.sync();

    if (searchResults.items.length > 0) {
        for (let i = 0; i < searchResults.items.length; i++) {
            searchResults.items[i].insertText(milestone.replace, Word.InsertLocation.replace);
        }
    }
}

// Also search for partial matches and replace them
const partialReplacements = [
    {find: "审核完成", replace: "Audit complete"},
    {find: "设计开始", replace: "Design start"},
    {find: "架构批准", replace: "Architecture approved"},
    {find: "开始", replace: "start"},
    {find: "验证", replace: "validated"},
    {find: "开发授权", replace: "Development authorized"},
    {find: "构建完整的", replace: "Build complete"},
    {find: "测试阶段", replace: "Testing phase"},
    {find: "培训开始实现上线", replace: "Go-live achieved → Training begins"},
    {find: "的员工接受了项目结束", replace: "staff trained → Project closure"}
];

for (let item of partialReplacements) {
    const searchResults = context.document.body.search(item.find, {matchCase: false});
    searchResults.load("items");
    await context.sync();

    if (searchResults.items.length > 0) {
        for (let i = 0; i < searchResults.items.length; i++) {
            searchResults.items[i].insertText(item.replace, Word.InsertLocation.replace);
        }
    }
}

await context.sync();
return "Key Milestones translated";

// Get all tables and find the Risk Mitigation table (second table)
const tables = context.document.body.tables;
tables.load("items");
await context.sync();

if (tables.items.length > 1) {
    const riskTable = tables.items[1];

    // Translate headers
    riskTable.getCell(0, 0).body.insertText("Risk", Word.InsertLocation.replace);
    riskTable.getCell(0, 1).body.insertText("Mitigation", Word.InsertLocation.replace);
    riskTable.getCell(0, 2).body.insertText("Contingency", Word.InsertLocation.replace);

    // Translate Risk column
    riskTable.getCell(1, 0).body.insertText("Technical", Word.InsertLocation.replace);
    riskTable.getCell(2, 0).body.insertText("Resources", Word.InsertLocation.replace);
    riskTable.getCell(3, 0).body.insertText("Timeline", Word.InsertLocation.replace);
    riskTable.getCell(4, 0).body.insertText("Adoption", Word.InsertLocation.replace);

    // Translate Mitigation column
    riskTable.getCell(1, 1).body.insertText("POC validation, phased approach", Word.InsertLocation.replace);
    riskTable.getCell(2, 1).body.insertText("Pre-approved contractors", Word.InsertLocation.replace);
    riskTable.getCell(3, 1).body.insertText("Phase gates, parallel work", Word.InsertLocation.replace);
    riskTable.getCell(4, 1).body.insertText("Comprehensive training", Word.InsertLocation.replace);

    // Translate Contingency column
    riskTable.getCell(1, 2).body.insertText("10% budget reserve", Word.InsertLocation.replace);
    riskTable.getCell(2, 2).body.insertText("Offshore options", Word.InsertLocation.replace);
    riskTable.getCell(3, 2).body.insertText("Built-in buffers", Word.InsertLocation.replace);
    riskTable.getCell(4, 2).body.insertText("Support team", Word.InsertLocation.replace);
}

await context.sync();
return "Risk Mitigation table translated";

// Find and translate the ROI box content
const roiSearch = context.document.body.search("回报：", {matchCase: false});
roiSearch.load("items");
await context.sync();

if (roiSearch.items.length > 0) {
    const roiPara = roiSearch.items[0].paragraphs.getFirst();
    roiPara.load("text");
    await context.sync();

    const newROIText = "Returns: $1.05M annual savings | 13.7-month payback | 162% 3-year ROI\nMetrics: 35% cost reduction | 50% speed increase | 99.9% uptime | 95% adoption";
    roiPara.insertText(newROIText, Word.InsertLocation.replace);
}

// Alternative approach - search for specific patterns
const roiPatterns = [
    {find: "回报：", replace: "Returns:"},
    {find: "每年节省", replace: "annual savings"},
    {find: "个月的投资回收率", replace: "-month payback"},
    {find: "年回报率", replace: "-year ROI"},
    {find: "指标：", replace: "Metrics:"},
    {find: "成本降低", replace: "cost reduction"},
    {find: "速度提高", replace: "speed increase"},
    {find: "正常运行时间", replace: "uptime"},
    {find: "采用率", replace: "adoption"}
];

for (let pattern of roiPatterns) {
    const searchResults = context.document.body.search(pattern.find, {matchCase: false});
    searchResults.load("items");
    await context.sync();

    if (searchResults.items.length > 0) {
        for (let i = 0; i < searchResults.items.length; i++) {
            searchResults.items[i].insertText(pattern.replace, Word.InsertLocation.replace);
        }
    }
}

await context.sync();
return "ROI and Success Metrics translated";

// Find and translate the Technology & Infrastructure paragraph
const techSearch = context.document.body.search("堆栈：", {matchCase: false});
techSearch.load("items");
await context.sync();

if (techSearch.items.length > 0) {
    const techPara = techSearch.items[0].paragraphs.getFirst();
    techPara.load("text");
    await context.sync();

    const newTechText = "Stack: Cloud-native platform, microservices architecture, API-first design, unified data lake, zero-trust security. Enables real-time analytics, AI/ML capabilities, seamless integration.";
    techPara.insertText(newTechText, Word.InsertLocation.replace);
}

// Alternative approach - translate specific terms
const techPatterns = [
    {find: "堆栈：", replace: "Stack:"},
    {find: "云原生平台", replace: "Cloud-native platform"},
    {find: "微服务架构", replace: "microservices architecture"},
    {find: "优先设计", replace: "-first design"},
    {find: "统一数据湖", replace: "unified data lake"},
    {find: "零信任安全", replace: "zero-trust security"},
    {find: "实现实时分析", replace: "Enables real-time analytics"},
    {find: "功能", replace: "capabilities"},
    {find: "无缝集成", replace: "seamless integration"}
];

for (let pattern of techPatterns) {
    const searchResults = context.document.body.search(pattern.find, {matchCase: false});
    searchResults.load("items");
    await context.sync();

    if (searchResults.items.length > 0) {
        for (let i = 0; i < searchResults.items.length; i++) {
            searchResults.items[i].insertText(pattern.replace, Word.InsertLocation.replace);
        }
    }
}

await context.sync();
return "Technology & Infrastructure translated";

// Get all tables and find the Team Structure table (third table)
const tables = context.document.body.tables;
tables.load("items");
await context.sync();

if (tables.items.length > 2) {
    const teamTable = tables.items[2];

    // Translate headers
    teamTable.getCell(0, 0).body.insertText("Role", Word.InsertLocation.replace);
    teamTable.getCell(0, 1).body.insertText("Count", Word.InsertLocation.replace);
    teamTable.getCell(0, 2).body.insertText("Focus", Word.InsertLocation.replace);

    // Translate Role column
    teamTable.getCell(1, 0).body.insertText("Project Management", Word.InsertLocation.replace);
    teamTable.getCell(2, 0).body.insertText("Development", Word.InsertLocation.replace);
    teamTable.getCell(3, 0).body.insertText("QA/Testing", Word.InsertLocation.replace);
    teamTable.getCell(4, 0).body.insertText("Business/UX/Data", Word.InsertLocation.replace);

    // Count column already has numbers

    // Translate Focus column
    teamTable.getCell(1, 2).body.insertText("Coordination, stakeholder management", Word.InsertLocation.replace);
    teamTable.getCell(2, 2).body.insertText("Core build, 3 AI/ML engineers", Word.InsertLocation.replace);
    teamTable.getCell(3, 2).body.insertText("Quality assurance, UAT", Word.InsertLocation.replace);
    teamTable.getCell(4, 2).body.insertText("Requirements, design, migration", Word.InsertLocation.replace);
}

await context.sync();
return "Team Structure table translated";

// Find and translate the Recommendation paragraph
const recSearch = context.document.body.search("这种转变对于", {matchCase: false});
recSearch.load("items");
await context.sync();

if (recSearch.items.length > 0) {
    const recPara = recSearch.items[0].paragraphs.getFirst();
    recPara.load("text");
    await context.sync();

    const newRecText = "This transformation is critical for RetailMax's competitiveness. The phased approach with clear gates ensures controlled execution. With proven ROI, comprehensive risk mitigation, and strategic alignment, we recommend immediate approval for Phase 1 in January 2025. The 35% cost reduction and 50% efficiency gains deliver immediate value and sustainable competitive advantage.";
    recPara.insertText(newRecText, Word.InsertLocation.replace);
}

// Alternative approach - translate specific patterns in the recommendation
const recPatterns = [
    {find: "这种转变对于", replace: "This transformation is critical for"},
    {find: "的竞争力至关重要", replace: "'s competitiveness"},
    {find: "具有清晰门的分阶段方法确保了受控执行", replace: "The phased approach with clear gates ensures controlled execution"},
    {find: "凭借经过验证的投资回报率", replace: "With proven ROI"},
    {find: "全面的风险缓解和战略调整", replace: "comprehensive risk mitigation, and strategic alignment"},
    {find: "我们建议立即在", replace: "we recommend immediate approval for Phase 1 in"},
    {find: "年", replace: ""},
    {find: "月批准第一阶段", replace: ""},
    {find: "的成本降低和", replace: "cost reduction and"},
    {find: "的效率提升带来了立竿见影的价值和可持续的竞争优势", replace: "efficiency gains deliver immediate value and sustainable competitive advantage"}
];

for (let pattern of recPatterns) {
    const searchResults = context.document.body.search(pattern.find, {matchCase: false});
    searchResults.load("items");
    await context.sync();

    if (searchResults.items.length > 0) {
        for (let i = 0; i < searchResults.items.length; i++) {
            searchResults.items[i].insertText(pattern.replace, Word.InsertLocation.replace);
        }
    }
}

await context.sync();
return "Recommendation section translated";
